const AvilableMeals = () => {
  const [availableMeals, setAvailableMeasls] = useState();
  const [error, setError] = useState();

  const fetchAvailableMeals = async () => {
    try {
      const response = await fetch(
        "https://food-order-app-905f2-default-rtdb.firebaseio.com/melas.json"
      );
      const data = await response.json();
      console.log(data);
      setAvailableMeasls(data);
      console.log("available meals:");
      console.log(availableMeals);

      if (!response.ok) {
        console.log("ok");
        throw new Error("Something went wrong!");
      }
    } catch (error) {
      setError(error.message);
    }
  };

  useEffect(() => {
    fetchAvailableMeals();
  }, [availableMeals]);

  // const mealsList = availableMeals.map((meal) => (
  //   <MealItem
  //     id={meal.id}
  //     key={meal.id}
  //     name={meal.name}
  //     description={meal.description}
  //     price={meal.price}
  //   />
  // ));
  return (
    <section className={classes.meals}>
      <Card>{/* <ul>{mealsList}</ul> */}</Card>
    </section>
  );
};

export default AvilableMeals;
