import Modal from "../UI/Modal";
import classes from "./Cart.module.css";
import { useContext, useState } from "react";
import CartContext from "../../store/cart-context";
import CartItem from "./CartItem";
import Checkout from "./Checkout";

const Cart = (props) => {
  const [isCheckout, setIsCheckout] = useState(false);
  const [poseRequestResponse, setPostRequestResponse] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const cartCtx = useContext(CartContext);

  const totalAmount = `$${cartCtx.amount.toFixed(2)}`;

  const hasItems = cartCtx.items.length > 0;

  const cartItems = (
    <ul className={classes["cart-items"]}>
      {cartCtx.items.map((item) => (
        <CartItem
          key={item.id}
          name={item.name}
          price={item.price}
          amount={item.amount}
          onRemove={() => {
            cartCtx.removeItem(item.id);
          }}
          onAdd={() => {
            cartCtx.addItem(item);
          }}
        />
      ))}
    </ul>
  );

  const orderHandler = () => {
    setIsCheckout(true);
  };

  const submitOrderHandler = async (userData) => {
    setIsLoading(true);
    const response = await fetch(
      "https://food-order-app-905f2-default-rtdb.firebaseio.com/eorders.json",
      {
        method: "POST",
        body: JSON.stringify({
          user: userData,
          orderItems: cartCtx.items,
        }),
      }
    );
    setIsLoading(false);
    setPostRequestResponse(response.ok);
  };

  const modalAction = (
    <div className={classes.actions}>
      <button onClick={props.onClose} className={classes["button--alt"]}>
        Close
      </button>
      {hasItems && (
        <button onClick={orderHandler} className={classes.button}>
          Order
        </button>
      )}
    </div>
  );

  return (
    <Modal onClose={props.onClose}>
      {cartItems}
      <div className={classes.total}>
        <span>Total Amount</span>
        <span>{totalAmount}</span>
      </div>
      {isCheckout && (
        <Checkout
          onConfirm={submitOrderHandler}
          responseCode={poseRequestResponse}
          onCancel={props.onClose}
          loadingMode={isLoading}
        />
      )}
      {!isCheckout && modalAction}
    </Modal>
  );
};

export default Cart;
