import CartContex from "./cart-context";
import { useReducer } from "react";

const defaultCartState = {
  items: [],
  amount: 0,
};

const cartReducer = (state, action) => {
  if (action.type === "ADD") {
    const updatedamount = state.amount + action.item.price * action.item.amount;

    const itemToUpdate = state.items.find((item) => {
      return item.id === action.item.id;
    });
    if (typeof itemToUpdate === "undefined") {
      const updatedItems = state.items.concat(action.item);
      return {
        items: updatedItems,
        amount: updatedamount,
      };
    } else {
      itemToUpdate.amount += 1;
      return {
        items: state.items,
        amount: updatedamount,
      };
    }
  }
  return defaultCartState;
};

const CartProvider = (props) => {
  const [cartState, dispatchCartAction] = useReducer(
    cartReducer,
    defaultCartState
  );

  const addItemToCart = (item) => {
    dispatchCartAction({ type: "ADD", item: item });
  };
  const removeItemFromCart = (id) => {
    dispatchCartAction({ type: "REMOVE", id: id });
  };

  const cartContext = {
    items: cartState.items,
    amount: cartState.amount,
    addItem: addItemToCart,
    removeItem: removeItemFromCart,
  };

  return (
    <CartContex.Provider value={cartContext}>
      {props.children}
    </CartContex.Provider>
  );
};

export default CartProvider;
